class Solution:
    def findSubstringInWraproundString(self, p: str) -> int:
        subs = []
        f, size = 0, 1
        for i in range(1, len(p)):
            if ord(p[i]) == ord(p[i-1])+1 or ord(p[i])-ord(p[i-1]) == -25:
                size += 1
            elif size == 1:
                subs.append(p[f:i+1])
                size = f = 0
    
        total = 0
        
        return total + int(size*(size+1)/2)
    
